icons = document.querySelectorAll(".icons"); let navTextItems = document.querySelectorAll(".nav-text-item"); let footTextItems = document.querySelectorAll(".foot-text-item"); let arrowTextItems = document.querySelectorAll(".arrow-text"); let footerButtons = document.querySelectorAll(".footer-button"); let canHover = !window.matchMedia("(hover: none)").matches; let upArrowButton = document.getElementById("up-arrow"); if (canHover) { icons.forEach(icon => { icon.addEventListener("mouseover", () => { navTextItems.forEach(navText => { if (`text-${icon.id}` === navText.id) navText.style.visibility = "visible"; }); footTextItems.forEach(footText => { if (`text-${icon.id}` === footText.id) footText.style.visibility = "visible"; }); arrowTextItems.forEach(arrowText => { if (`text-${icon.id}` === arrowText.id) arrowText.style.visibility = "visible"; }); }); icon.addEventListener("mouseleave", () => { navTextItems.forEach(navText => { if (`text-${icon.id}` === navText.id) navText.style.visibility = "hidden"; }); footTextItems.forEach(footText => { if (`text-${icon.id}` === footText.id) footText.style.visibility = "hidden"; }); arrowTextItems.forEach(arrowText => { if (`text-${icon.id}` === arrowText.id) arrowText.style.visibility = "hidden"; }); }); }); } footerButtons.forEach(button => button.addEventListener("click", () => button.id === "sun-button" ? toggleTemp("light") : toggleTemp("dark"), ), ); if (upArrowButton) { upArrowButton.addEventListener("click", () => { window.scrollTo({ top: 0, behavior: "smooth" }); }); } toggleTemp = theme => { icons.forEach(icon => icon.setAttribute("data-theme", `${theme}`)); document.documentElement.setAttribute("data-theme", `${theme}`); localStorage.setItem("data-theme", `${theme}`); }; window.onload = () => { if (!localStorage["data-theme"]) window.matchMedia("(prefers-color-scheme: light)").matches ? toggleTemp("light") : toggleTemp("dark"); else localStorage["data-theme"] === "light" ? toggleTemp("light") : toggleTemp("dark"); }; new MutationObserver(() => { let isDarkReaderEnabled = "querySelector" in document && !!document.querySelector("meta[name=darkreader]"); isDarkReaderEnabled ? toggleTemp("dark") : toggleTemp("light"); }).observe(document.querySelector("head"), { childList: true });