let icons = document.querySelectorAll(".icons"); let toggleTemp = theme => { icons.forEach(icon => icon.setAttribute("data-theme", `${theme}`)); document.documentElement.setAttribute("data-theme", `${theme}`); localStorage.setItem("data-theme", `${theme}`); }; if (!localStorage["data-theme"]) window.matchMedia("(prefers-color-scheme: light)").matches ? toggleTemp("light") : toggleTemp("dark"); else localStorage["data-theme"] === "light" ? toggleTemp("light") : toggleTemp("dark");